mapper.select:
SELECT a.*,
list.label,
b.audit_status, c.id table_state, c.table_name FROM ${map.tableName} as a inner join `table` d on a.table_id = d.id INNER JOIN audit b ON a.table_id = b.table_id INNER JOIN table_state c ON b.proposer_type = c.id
inner join list on a.name = list.id
and a.type = 1
and a.type = 2
and b.proposer_id = #{map.userId}
and a.`name` like "%"#{map.name}"%"
and d.`id` = #{map.tableId}
and a.`name` like "%"#{map.keywords}"%"
and `table_name` = #{map.table_name}
and `create_time` between #{map.starTime} and #{map.endTime}
and `audit_status` = #{map.audit_status}

SELECT a.*,
list.label,
b.audit_status, c.id table_state, c.table_name FROM ${map.tableName} as a inner join `table` d on a.table_id = d.id INNER JOIN audit b ON a.table_id = b.table_id INNER JOIN table_state c ON b.proposer_type = c.id
inner join list on a.name = list.id
and b.proposer_id = #{map.userId}
and a.`name` like "%"#{map.name}"%"
and d.`id` = #{map.tableId}
and a.`name` like "%"#{map.keywords}"%"
and `table_name` = #{map.table_name}
and `create_time` between #{map.starTime} and #{map.endTime}
and `audit_status` = #{map.audit_status}

select * from table_state


        and state = #{state}


        and id = #{id}


        and state in (2,4,6) or id in (2,4,6)


        and state in (1,3,5) or id in (1,3,5)

SELECT a.* FROM ( SELECT id FROM `table` WHERE user_id IN ( SELECT user_id FROM user_details WHERE `name` LIKE "%"#{map.username}"%" ) AND state = #{map.option} ) b INNER JOIN ( SELECT * FROM ${map.tableName} ) a ON a.table_id = b.id
and a.NAME LIKE "%"#{map.name}"%"
and `create_time` between #{map.starTime} and #{map.endTime}
order by create_time
limit #{map.start},#{map.size}
